import React, { useState } from "react";
import { ProductModel } from "../../models/product.model";
import { useForm } from "react-hook-form";
import { useDispatch } from "react-redux";
import { addNewProduct } from "../../redux/slices/products.slices";
import { useNavigate } from "react-router-dom";

// type NewProductProps = {
//   AddANewProduct: (newProduct: ProductModel) => void;
// };

const NewProductWithHookForm = () => {
  let dispatch = useDispatch();
  let navigate = useNavigate();
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<ProductModel>({ mode: "all" }); // returns an object
  return (
    <>
      <header>
        <h1>New Product</h1>
      </header>
      <div className="d-flex justify-content-center align-items-center m-2">
        <form
          onSubmit={handleSubmit(
            ({ id, title, price, rating, imageUrl, description, likes }) => {
              dispatch(
                addNewProduct({
                  id,
                  title,
                  price,
                  rating,
                  imageUrl,
                  description,
                  likes,
                }),
              );

              // dispatch(addNewProduct(newProduct));
              navigate("/dashboard");
            },
          )}
        >
          <div className="row my-1">
            <div className="col-md-4">
              <label htmlFor="txtProductId">Id : </label>
            </div>
            <div className="col-md-4">
              <input
                type="number"
                id="txtProductId"
                {...register("id", { required: true })}
              />

              {errors.id && <p style={{ color: "red" }}>Id is Required!</p>}
            </div>
          </div>
          <div className="row my-1">
            <div className="col-md-4">
              <label htmlFor="txtProductTitle">Title : </label>
            </div>
            <div className="col-md-4">
              <input
                type="text"
                id="txtProductTitle"
                {...register("title", {
                  required: true,
                  maxLength: {
                    value: 20,
                    message: "You exceeded 20 chars limit!",
                  },
                })}
              />
              {errors.title && (
                <p style={{ color: "red" }}>
                  {errors.title.type === "maxLength"
                    ? errors.title.message
                    : "Title is Required!"}
                </p>
              )}
            </div>
          </div>
          <div className="row my-1">
            <div className="col-md-4">
              <label htmlFor="txtProductPrice">Price : </label>
            </div>
            <div className="col-md-4">
              <input
                type="number"
                id="txtProductPrice"
                {...register("price")}
              />
            </div>
          </div>
          <div className="row my-1">
            <div className="col-md-4">
              <label htmlFor="txtProductLikes">Likes : </label>
            </div>
            <div className="col-md-4">
              <input
                type="number"
                id="txtProductLikes"
                {...register("likes")}
              />
            </div>
          </div>
          <div className="row my-1">
            <div className="col-md-4">
              <label htmlFor="txtProductRating">Rating : </label>
            </div>
            <div className="col-md-4">
              <input
                type="number"
                id="txtProductRating"
                {...register("rating")}
              />
            </div>
          </div>
          <div className="row my-1">
            <div className="col-md-4">
              <label htmlFor="txtProductImage">Imageurl : </label>
            </div>
            <div className="col-md-4">
              <input
                type="text"
                id="txtProductImage"
                {...register("imageUrl")}
              />
            </div>
          </div>
          <div className="row my-1">
            <div className="col-md-4">
              <label htmlFor="txtProductDescription">Description : </label>
            </div>
            <div className="col-md-4">
              <textarea
                id="txtProductDescription"
                cols={25}
                rows={5}
                {...register("description")}
              ></textarea>
            </div>
          </div>
          <div className="row my-1">
            <div className="col-md-4"></div>
            <div className="col-md-6">
              <button className="btn btn-success">Add Product</button>
            </div>
          </div>
        </form>
      </div>
    </>
  );
};

export default NewProductWithHookForm;

